import React from "react";
import Row from "./Row";

const Modal = (props) => {
  return (
    <div className="modal">
      <Row text="Font Color" hexCodeClr="#44444" />
      <Row text="Backgrounf Color" hexCodeClr="#FFFFF" />
      <Row text="Button Color" hexCodeClr="#2027EF" />
      <Row text="Button Border Color" hexCodeClr="##2027EF" />
      <Row text="Font Color" hexCodeClr="#44444" />
      <div>
        <button className="btn btn--alt" onClick={props.click}>
          Cancel
        </button>
        <button className="btn" onClick={props.click}>
          Save
        </button>
      </div>
    </div>
  );
};

export default Modal;
